import React, { useState } from 'react';
import './.css'; // Ensure the CSS file path is correct

const FeedbackForm = () => {
  const [formData, setFormData] = useState({
    category: '',
    heading: '',
    summary: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here (e.g., send data to backend)
    console.log('Form submitted:', formData);
    alert('Feedback submitted successfully!');
    setFormData({
      category: '',
      heading: '',
      summary: '',
    });
  };

  return (
    <div className="feedback-form-container">
      <h1 className="form-title">Submit an Issue</h1>
      <form className="feedback-form" onSubmit={handleSubmit}>
        <div className="form-grid">
          {/* Category Section */}
          <div className="form-section">
            <label>
              Category:
              <select name="category" value={formData.category} onChange={handleChange} required>
                <option value="">Select Category</option>
                <option value="General">General</option>
                <option value="Room Issue">Room Issue</option>
                <option value="Maintenance">Maintenance</option>
                <option value="Food Services">Food Services</option>
                <option value="Other">Other</option>
              </select>
            </label>
          </div>

          {/* Heading Section */}
          <div className="form-section">
            <label>
              Heading:
              <input
                type="text"
                name="heading"
                value={formData.heading}
                onChange={handleChange}
                placeholder="Enter issue heading"
                required
              />
            </label>
          </div>

          {/* Summary Section */}
          <div className="form-section">
            <label>
              Summary:
              <textarea
                name="summary"
                value={formData.summary}
                onChange={handleChange}
                placeholder="Provide a brief summary of the issue"
                required
              ></textarea>
            </label>
          </div>
        </div>

        <button type="submit" className="form-button">Submit Issue</button>
      </form>
    </div>
  );
};

export default FeedbackForm;
