// import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Outsiders = () => {
    const [outsiders, setOutsiders] = useState([]);
    const [newOutsider, setNewOutsider] = useState({
        fullName: '',
        age: '',
        gender: '',
        contactNumber: '',
        address: '',
        dob: '',
        guardianName: '',
        guardianContact: '',
        purposeOfStay: '',
        physicalLimitations: ''
    });

    useEffect(() => {
        // Fetch outsiders when component mounts
        fetchOutsiders();
    }, []);

    const fetchOutsiders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/outsider/api/outsiders');
            setOutsiders(response.data);
        } catch (error) {
            console.error('Error fetching outsiders:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewOutsider((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8080/outsider/api/outsiders', newOutsider);
            setNewOutsider({
                fullName: '',
                age: '',
                gender: '',
                contactNumber: '',
                address: '',
                dob: '',
                guardianName: '',
                guardianContact: '',
                purposeOfStay: '',
                physicalLimitations: ''
            });
            fetchOutsiders(); // Refresh the list after adding
        } catch (error) {
            console.error('Error adding outsider:', error);
        }
    };

    return (
        <div>
            <h1>Outsiders List</h1>
            <ul>
                {outsiders.map(outsider => (
                    <li key={outsider.id}>
                        {outsider.fullName} - {outsider.age} - {outsider.gender}
                    </li>
                ))}
            </ul>

            <h2>Add Outsider</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="fullName" placeholder="Full Name" value={newOutsider.fullName} onChange={handleChange} required />
                <input type="number" name="age" placeholder="Age" value={newOutsider.age} onChange={handleChange} required />
                <input type="text" name="gender" placeholder="Gender" value={newOutsider.gender} onChange={handleChange} />
                <input type="text" name="contactNumber" placeholder="Contact Number" value={newOutsider.contactNumber} onChange={handleChange} />
                <input type="text" name="address" placeholder="Address" value={newOutsider.address} onChange={handleChange} />
                <input type="date" name="dob" value={newOutsider.dob} onChange={handleChange} />
                <input type="text" name="guardianName" placeholder="Guardian Name" value={newOutsider.guardianName} onChange={handleChange} />
                <input type="text" name="guardianContact" placeholder="Guardian Contact" value={newOutsider.guardianContact} onChange={handleChange} />
                <input type="text" name="purposeOfStay" placeholder="Purpose of Stay" value={newOutsider.purposeOfStay} onChange={handleChange} />
                <input type="text" name="physicalLimitations" placeholder="Physical Limitations" value={newOutsider.physicalLimitations} onChange={handleChange} />
                <button type="submit">Add Outsider</button>
            </form>
        </div>
    );
};

export default Outsiders;
