import React, { useState } from 'react';

const RegistrationTable = ({ data = [] }) => {
  const [searchTerm, setSearchTerm] = useState('');  // State to hold the search term
  const totalRows = 10;  // Define the number of rows to display

  // Filter data based on the search term
  const filteredData = data.filter((item) =>
    Object.values(item).some((value) =>
      value.toString().toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  // Limit displayed rows to totalRows or the number of filtered items if less
  const rows = filteredData.slice(0, totalRows);
  const emptyRows = totalRows - rows.length;

  return (
    <div>
      {/* Wrapper for title and search bar */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
        <h2 style={{ margin: 0 }}>Registration Data</h2>

        {/* Search bar */}
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={searchBarStyle}
        />
      </div>

      <table style={tableStyle}>
        <thead>
          <tr>
            {[
              "Room", "Sector", "Fee Per Month", "Food Choice", "Stay From", "Duration", "Course", 
              "Reg No", "First Name", "Middle Name", "Last Name", "Gender", "Contact", 
              "Email", "Emer Contact", "Guar Name", "Guar Relation", "Guar Contact", 
              "Per Address", "Per City", "Per State", "Per Pincode"
            ].map((header, index) => (
              <th key={index} style={headerStyle}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((item, index) => (
            <tr key={index} style={{ backgroundColor: index % 2 === 0 ? '#ffffff' : '#eaf4fb' }}>
              <td style={cellStyle}>{item.Room}</td>
              <td style={cellStyle}>{item.Sector}</td>
              <td style={cellStyle}>{item.FeePerMonth}</td>
              <td style={cellStyle}>{item.FoodChoice}</td>
              <td style={cellStyle}>{item.StayFrom}</td>
              <td style={cellStyle}>{item.Duration}</td>
              <td style={cellStyle}>{item.course}</td>
              <td style={cellStyle}>{item.RegNo}</td>
              <td style={cellStyle}>{item.FirstName}</td>
              <td style={cellStyle}>{item.MiddleName}</td>
              <td style={cellStyle}>{item.LastName}</td>
              <td style={cellStyle}>{item.Gender}</td>
              <td style={cellStyle}>{item.Contact}</td>
              <td style={cellStyle}>{item.Email}</td>
              <td style={cellStyle}>{item.EmerContact}</td>
              <td style={cellStyle}>{item.GuarName}</td>
              <td style={cellStyle}>{item.GuarRelation}</td>
              <td style={cellStyle}>{item.GuarContact}</td>
              <td style={cellStyle}>{item.PerAddress}</td>
              <td style={cellStyle}>{item.PerCity}</td>
              <td style={cellStyle}>{item.PerState}</td>
              <td style={cellStyle}>{item.PerPincode}</td>
            </tr>
          ))}
          {/* Add empty rows if data is less than totalRows */}
          {emptyRows > 0 && (
            Array.from({ length: emptyRows }, (_, index) => (
              <tr key={`empty-row-${index}`} style={{ backgroundColor: '#ffffff' }}>
                {Array.from({ length: 22 }).map((_, colIndex) => (
                  <td key={colIndex} style={cellStyle}>&nbsp;</td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

const searchBarStyle = {
  width: '200px', // Adjust width as needed
  padding: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
  fontSize: '16px',
};

const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  tableLayout: 'fixed',
  backgroundColor: '#f3f3f3', // Light background like in the image
};

const headerStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  backgroundColor: '#007BA7', // Header background color similar to the image
  color: '#ffffff',
  fontWeight: 'bold',
  textAlign: 'center', // Center align header text
};

const cellStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  textAlign: 'center',
  color: '#333',
};

export default RegistrationTable;
