import React, { useState } from 'react';
import './registration.css'; // Import the CSS file
import OutsidersRegistrationTable from './outsiders-data';

const HostelRegistration = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        age: '',
        gender: '',
        contactNumber: '',
        address: '',
        dob: '',
        guardianName: '',
        guardianContact: '',
        purposeOfStay: '',
        physicalLimitations: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData); // Here you can handle form submission, e.g., send data to an API
        alert('Registration successful!');
    
        fetch("http://localhost:8080/outsider/add", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(formData) // Send formData to the backend
        })
        .then(response => {
            if (response.ok) {
                console.log("New outsider added");
            } else {
                console.error("Failed to add outsider");
            }
        })
        .catch(error => console.error("Error:", error));
    };
    
       
    

    return (
        <div className="registration-form">
            <h2>Outsiders Registration</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="fullName" placeholder="Full Name" onChange={handleChange} required />
                <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
                <select name="gender" onChange={handleChange} required>
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                </select>
                <input type="text" name="contactNumber" placeholder="Contact Number" onChange={handleChange} required />
                <input type="text" name="address" placeholder="Address" onChange={handleChange} required />
                <input type="date" name="dob" onChange={handleChange} required />
                <input type="text" name="guardianName" placeholder="Guardian Name" onChange={handleChange} required />
                <input type="text" name="guardianContact" placeholder="Guardian Contact Number" onChange={handleChange} required />
                <textarea name="purposeOfStay" placeholder="Purpose of Stay" onChange={handleChange} required />
                <textarea name="physicalLimitations" placeholder="Physical Limitations (if any)" onChange={handleChange} />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default HostelRegistration;
