import React, { useState } from 'react';
import './feedback.css'; // Ensure the CSS file path is correct

const FeedbackForm = () => {
  const [formData, setFormData] = useState({
    registerNumber: '', // New field for register number
    category: '',
    heading: '',
    summary: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here (e.g., send data to backend)
    console.log('Form submitted:', formData);
    alert('Feedback submitted successfully!');
    setFormData({
      registerNumber: '', // Reset register number after submission
      category: '',
      heading: '',
      summary: '',
    });
  };

  return (
    <div className="feedback-form-container">
      <h1 className="form-title">FEEDBACK</h1>
      <form className="feedback-form" onSubmit={handleSubmit}>
        <div className="form-grid">
          
          {/* Register Number Section */}
          <div className="form-section">
            <label className="form-label">
              Register Number:
              <input
                type="text"
                name="registerNumber"
                value={formData.registerNumber}
                onChange={handleChange}
                placeholder="Enter your register number"
                required
                className="form-input"
              />
            </label>
          </div>

          {/* Category Section */}
          <div className="form-section">
            <label className="form-label">
              Category:
              <select 
                name="category" 
                value={formData.category} 
                onChange={handleChange} 
                required 
                className="form-input"
              >
                <option value="">Select Category</option>
                <option value="General">General</option>
                <option value="Room Issue">Room Issue</option>
                <option value="Maintenance">Maintenance</option>
                <option value="Food Services">Food Services</option>
                <option value="Other">Other</option>
              </select>
            </label>
          </div>

          {/* Heading Section */}
          <div className="form-section">
            <label className="form-label">
              Heading:
              <input
                type="text"
                name="heading"
                value={formData.heading}
                onChange={handleChange}
                placeholder="Enter issue heading"
                required
                className="form-input"
              />
            </label>
          </div>

          {/* Summary Section */}
          <div className="form-section">
            <label className="form-label">
              Summary:
              <textarea
                name="summary"
                value={formData.summary}
                onChange={handleChange}
                placeholder="Provide a brief summary of the issue"
                required
                className="form-input"
              ></textarea>
            </label>
          </div>
        </div>

        <button type="submit" className="form-button">Submit Issue</button>
      </form>
    </div>
  );
};

export default FeedbackForm;
