import React, { useState } from 'react';
import './feedback.css'; // Ensure the CSS file path is correct

const Feedbackform = () => {
  const [formData, setFormData] = useState({
    registerNumber: '',
    category: '',
    heading: '',
    summary: '',
  });
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert('Feedback submitted successfully!');
    setFormData({
      registerNumber: '',
      category: '',
      heading: '',
      summary: '',
    });
  };

  const formFields = [
    { label: "Register Number", name: "registerNumber", type: "text", placeholder: "Enter your register number" },
    { label: "Category", name: "category", type: "select", options: ["General", "Room Issue", "Maintenance", "Food Services", "Other"] },
    { label: "Heading", name: "heading", type: "text", placeholder: "Enter issue heading" },
    { label: "Summary", name: "summary", type: "textarea", placeholder: "Provide a brief summary of the issue" },
  ];

  return (
    <div className="feedback-form-container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
        <h1 style={{ margin: 0 }}>Feedback Form</h1>
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={searchBarStyle}
        />
      </div>

      <form className="feedback-form" onSubmit={handleSubmit} style={formTableStyle}>
        <table>
          <tbody>
            {formFields.map((field, index) => (
              <tr key={index}>
                <td style={headerStyle}>{field.label}</td>
                <td style={cellStyle}>
                  {field.type === "text" && (
                    <input
                      type="text"
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                      placeholder={field.placeholder}
                      required
                      className="form-input"
                    />
                  )}
                  {field.type === "select" && (
                    <select
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                      required
                      className="form-input"
                    >
                      <option value="">Select {field.label}</option>
                      {field.options.map((option, idx) => (
                        <option key={idx} value={option}>{option}</option>
                      ))}
                    </select>
                  )}
                  {field.type === "textarea" && (
                    <textarea
                      name={field.name}
                      value={formData[field.name]}
                      onChange={handleChange}
                      placeholder={field.placeholder}
                      required
                      className="form-input"
                    />
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="submit" className="form-button">Submit Issue</button>
      </form>
    </div>
  );
};

const searchBarStyle = {
  width: '200px',
  padding: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
  fontSize: '16px',
};

const formTableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  tableLayout: 'fixed',
  marginTop: '15px',
};

const headerStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  backgroundColor: '#007BA7',
  color: '#ffffff',
  fontWeight: 'bold',
  textAlign: 'left',
  width: '30%',
};

const cellStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  textAlign: 'left',
  color: '#333',
};

export default Feedbackform;
