// outsiders.jsx
import React, { useState } from 'react';
import './registration.css';

const HostelRegistration = ({ fetchOutsiders }) => {
    const [formData, setFormData] = useState({
        fullName: '',
        age: '',
        gender: '',
        contactNumber: '',
        address: '',
        dob: '',
        guardianName: '',
        guardianContact: '',
        purposeOfStay: '',
        physicalLimitations: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        fetch("http://localhost:8080/outsider/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
        })
        .then(response => {
            if (response.ok) {
                alert('Outsider added successfully!');
                setFormData({
                    fullName: '',
                    age: '',
                    gender: '',
                    contactNumber: '',
                    address: '',
                    dob: '',
                    guardianName: '',
                    guardianContact: '',
                    purposeOfStay: '',
                    physicalLimitations: ''
                });
                fetchOutsiders(); // Refresh the list of outsiders
            } else {
                alert('Failed to add outsider');
            }
        })
        .catch(error => {
            console.error("Error:", error);
            alert('An error occurred while adding the outsider');
        });
    };

    return (
        <div className="registration-form">
            <h2>Outsiders Registration</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} required />
                <input type="number" name="age" placeholder="Age" value={formData.age} onChange={handleChange} required />
                <select name="gender" value={formData.gender} onChange={handleChange} required>
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                </select>
                <input type="text" name="contactNumber" placeholder="Contact Number" value={formData.contactNumber} onChange={handleChange} required />
                <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
                <input type="date" name="dob" value={formData.dob} onChange={handleChange} required />
                <input type="text" name="guardianName" placeholder="Guardian Name" value={formData.guardianName} onChange={handleChange} required />
                <input type="text" name="guardianContact" placeholder="Guardian Contact" value={formData.guardianContact} onChange={handleChange} required />
                <textarea name="purposeOfStay" placeholder="Purpose of Stay" value={formData.purposeOfStay} onChange={handleChange} required />
                <textarea name="physicalLimitations" placeholder="Physical Limitations (if any)" value={formData.physicalLimitations} onChange={handleChange} />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default HostelRegistration;
